def test_extraction():
    """Test AI extraction on real Costco HTML"""
    
    print("=" * 60)
    print("🧪 Testing AI Product Extraction")
    print("=" * 60)
    
    client = OpenAI(api_key=config.OPENAI_API_KEY)
    
    prompt = f"""
Extract the product information from this Costco product page HTML.

Find and extract:
- name: Full product name (look for h1 tag or product title)
- costco_sku: Item number (look for "Item" followed by 7 digits)
- model: Model number if present (like "MW123LL/A")
- costco_price: Current price as number (the actual price, not "was" price)
- original_price: If there's a discount, what was the original price?
- discount_amount: Dollar amount of discount if shown
- category: Product category (from breadcrumbs if present)
- in_stock: Is it available? (look for stock status)

Return ONLY valid JSON object. No markdown, no explanation.

Example format:
{{
  "name": "MacBook Air Laptop...",
  "costco_sku": "1814012",
  "model": "MW123LL/A",
  "costco_price": 749.99,
  "original_price": 949.99,
  "discount_amount": 200,
  "category": "Computers",
  "in_stock": false
}}

HTML:
{SAMPLE_HTML}
"""
    
    print("\n🤖 Calling OpenAI API...")
    print(f"📊 HTML length: {len(SAMPLE_HTML)} characters\n")
    
    try:
        response = client.chat.completions.create(
            model="gpt-4o-mini",
            messages=[
                {"role": "system", "content": "You are a data extraction expert. Extract product information from HTML and return ONLY valid JSON."},
                {"role": "user", "content": prompt}
            ],
            temperature=0
        )
        
        ai_response = response.choices[0].message.content
        
        print("✅ OpenAI responded!\n")
        print("📝 Raw AI Response:")
        print("-" * 60)
        print(ai_response[:500])
        print("-" * 60)
        
        # Clean response
        if '```json' in ai_response:
            ai_response = ai_response.split('```json')[1].split('```')[0]
        elif '```' in ai_response:
            ai_response = ai_response.split('```')[1].split('```')[0]
        
        product = json.loads(ai_response.strip())
        
        print("\n" + "=" * 60)
        print("✅ EXTRACTION SUCCESS!")
        print("=" * 60)
        print(f"\n📦 Product: {product['name']}")
        print(f"🏷️  Item #: {product['costco_sku']}")
        print(f"📋 Model: {product.get('model', 'N/A')}")
        print(f"💰 Price: ${product['costco_price']}")
        print(f"💵 Was: ${product.get('original_price', 'N/A')}")
        print(f"🎉 Discount: ${product.get('discount_amount', 0)}")
        print(f"📂 Category: {product.get('category', 'N/A')}")
        print(f"📦 In Stock: {product.get('in_stock', 'Unknown')}")
        
        print("\n" + "=" * 60)
        print("🎯 AI EXTRACTION WORKS!")
        print("=" * 60)
        
        return product
        
    except json.JSONDecodeError as e:
        print(f"\n❌ JSON parsing failed: {e}")
        print(f"Response was: {ai_response[:1000]}")
        return None
    
    except Exception as e:
        print(f"\n❌ Error: {e}")
        import traceback
        traceback.print_exc()
        return None

if __name__ == "__main__":
    result = test_extraction()
    
    if result:
        print("\n✨ Next step: Save this to Airtable!")
    else:
        print("\n⚠️  Need to adjust the prompt")
